mapper.delete:
DELETE
FROM blog_collect
WHERE blog_id = #{blogId}
  AND collector_id = #{bloggerId}

mapper.select:
SELECT b.id, b.blog_id, b.collector_id, b.reason, b.collect_date, b.category_id FROM blog_collect b left join blog_statistics s on s.blog_id = b.blog_id WHERE b.collector_id = #{bloggerId}
AND b.category_id = #{category}
order by s.${sortRule.rule.code} ${sortRule.order.code}

SELECT count(*)
FROM blog_collect
WHERE collector_id = #{value}

SELECT *
FROM blog_collect
WHERE blog_id = #{blogId}
  AND collector_id = #{bloggerId}

SELECT blog_id
FROM blog_collect
WHERE collector_id = #{value}
